import React, { Component } from 'react';
import * as d3 from "d3";

export class GridStructure extends Component {
    constructor(props, gridWidth, gridHeight, rows, cols, padding, margin, id) {
        super(props)

        this.gridWidth = 30;

        this.gridHeight = 30;
        this.rows = 10;
        this.cols = 10;
        this.width = this.gridWidth * this.cols;
        this.height = this.gridHeight * this.rows;
        this.padding = 0.05;
        this.margin = {
            top: 20, bottom: 20, left: 20, right: 20
        };
        this.id = "text"

        this.svg = null;

        this.drawLine = this.drawLine.bind(this);
        this.visulizeBox = this.visulizeBox.bind(this);
    }

    componentDidMount() {
        this.svg = d3.select("#" + this.id).append("svg")
                    .attr("width", this.width + this.margin.left + this.margin.right)
                    .attr("height", this.height + this.margin.top + this.margin.bottom);

        this.drawLine(0, this.height, 0, 0, "gray")
        this.visulizeBox(1, 1, 2, 2, 1, "ID-2", "blue", "yellow")
    }

    drawLine(x1, y1, x2, y2, color) {
        this.svg.append("line").style("stroke", color)
                        .attr("x1", this.getX(x1)).attr("y1", this.getY(y1))
                        .attr("x2", this.getX(x2)).attr("y2", this.getY(y2))
    }

    visulizeBox(row, col, widthGrid, heightGrid, text, id, boxColor, textColor) {
        let width = (1 - 2 * this.padding) * this.gridWidth * widthGrid;
        let height = (1 - 2 * this.padding) * this.gridHeight * heightGrid;
        let x = (col - 1 + this.padding) * this.gridWidth;
        let y = (row - 1 + this.padding) * this.gridHeight;

        let rectGroup = this.svg.append("g")
            .attr("id", id)
            .attr("transform", "translate(" + this.getX(x) + ", " + this.getY(y) + ")")

        let textX = (0.5 - this.padding) * this.gridWidth * widthGrid;
        let textY = (0.5 - this.padding) * this.gridHeight * heightGrid; 
        let textSize = 1 + "rem";

        rectGroup.append("rect")
                .attr("rx", 5).attr("ry", 5)
                .attr("width", width).attr("height", height).attr("fill", boxColor)

        rectGroup.append("text").attr("fill", textColor)
                    .style("text-anchor", "middle")
                    .style("alignment-baseline", "central")
                    .attr("font-size", textSize)
                    .attr("dy", textY)
                    .attr("dx", textX)
                    .text(text)
    }

    getX(x) {
        return(x + this.margin.left);
    }

    getY(y) {
        return(y + this.margin.top)
    }

    getSVG() {
        return(this.svg)
    }

    render() {
        return(
            <div id={this.id}>Basic Grid Structure</div>
        )
    }
}
