import React, { Component } from 'react';
import * as d3 from "d3";
import {GridStructure} from './GridStructure'

export class Insertion extends GridStructure {
    constructor(props) {
        super(props, 30, 30, 3, 10, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "insertion")

        this.svg = null;

        this.getInitialPosition = this.getInitialPosition.bind(this);

        this.separatorInitial = {
            row: 1,
            col: 1
        }

        let initArray = [3, 2, 6, 2, 9, 8]

        this.state = {
            array: initArray,
            actions: [],
            step: 0,
            speed: 200,
            setNewArray: false,
            animation: false
        }
    }


    componentDidMount() {
        this.svg = this.getSVG();
        this.drawBasicStructure();
        this.initViz();
        this.setState({
            //actions: this.getAction()
        })
    }

    clearViz() {
        this.svg.selectAll("g").remove();
    }

    initViz() {
        let positionMap = this.getInitialPosition();

        for (let i = 0; i < this.state.array.length; i++) {
            let position = positionMap[i]
            let num = this.state.array[i]
            let id = "insertion-" + i
            this.visulizeBox(position.row, position.col, 1, 1, num, id)
        }

        this.drawSeparator(this.separatorInitial.row, this.separatorInitial.col);
        this.drawIndicator(this.indicatorInitial.row, this.indicatorInitial.col, "min");
    }

    getInitialPosition(array = this.state.array) {
        let positionMap = {}
        for (let i = 0; i < array.length; i++) {
            positionMap[i] = {
                row: 2,
                col: i + 2
            }
        } 
        return(positionMap)
    }

    render() {
        return(
            <div></div>
        )
    }

}
