import React from 'react';
import {GridStructure} from './GridStructure'

export class SortDemo extends GridStructure {
    constructor(props) {
        super(props, 30, 30, 3, 10, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "SortDemo")

        let initArray = [3, 2, 6, 2, 9, 8, 4]

        this.svg = null;
        this.initViz = this.initViz.bind(this)

        this.state = {
            array: initArray
        }
    }

    componentDidMount() {
        this.svg = this.getSVG();
        this.drawBasicStructure();
        this.initViz();
    }

    initViz() {
        this.visulizeBox(1, 1, 2, 1, "Input", "Input-Tag", "gray", "white")
        this.visulizeBox(3, 1, 2, 1, "Output", "Output-Tag", "green", "white")
        this.drawLine(57, 0, 57, 90)
    }

    render() {
        return(
            <div id="SortDemo"></div>
        )
    }
}
