import React, { Component } from 'react';
import * as d3 from "d3";

export class GridStructure extends Component {
    constructor(props, gridWidth = 30, gridHeight = 30, rows = 5, cols = 5, padding = 0.05, margin = {
        top: 20, botttom: 20, left: 20, right: 20
    }) {
        super(props)

        this.gridWidth = gridWidth;
        this.gridHeight = gridHeight;
        this.rows = rows;
        this.cols = cols;
        this.width = gridWidth * cols;
        this.height = gridHeight * rows;
        this.padding = padding;
        this.margin = margin;

        this.svg = null;

        this.drawLine = this.drawLine.bind(this);
    }

    componentDidMount() {
        this.svg = d3.select("body").append("svg")
                        .attr("width", this.width + this.margin.left + this.margin.right)
                        .attr("height", this.height + this.margin.top + this.margin.bottom);
        this.drawLine(0, this.height, 0, 0)
    }

    drawLine(x1, y1, x2, y2, color) {
        this.svg.append("line").style("stroke", color)
                        .attr("x1", this.getX(x1)).attr("y1", this.getY(y1))
                        .attr("x2", this.getX(x2)).attr("y2", this.getY(y2))
    }

    getX(x) {
        return(x + this.margin.left);
    }

    getY(y) {
        return(y + this.margin.top)
    }

    getSVG() {
        return(this.svg)
    }

    render() {
        return(
            <div>Basic Grid Structure</div>
        )
    }
}
