import React, { Component } from 'react';
import * as d3 from "d3";
import {GridStructure} from './GridStructure'


export class SelectionSort extends GridStructure {
    constructor(props) {
        // 1: Grid Width 30px
        // 2: Grid Height 30px
        // 3: nRow: 3
        // 4: nCol: 10
        // 5: padding: 0.05
        // 6: margin: top: 20px, bottom: 20px, left: 20px, right: 20px


        super(props, 30, 30, 3, 10, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "selection")

        this.svg = null;

        this.getInitialPosition = this.getInitialPosition.bind(this);
        this.getAction = this.getAction.bind(this);

        this.state = {
            array: [3, 2, 6, 2, 9, 8],
            action: []    
        }

        this.indicatorInitial = {
            row: 3,
            col: 10
        }

        this.separatorIndicator = {
            row: 1,
            col: 2
        }
    }

    getInitialPosition(array = this.state.array) {
        let positionMap = {}
        for (let i = 0; i < array.length; i++) {
            positionMap[i] = {
                row: 2,
                col: i + 3
            }
        } 
        return(positionMap)
    }

    componentDidMount() {
        this.svg = this.getSVG();
        this.drawBasicStructure();

        let positionMap = this.getInitialPosition();

        for (let i = 0; i < this.state.array.length; i++) {
            let position = positionMap[i]
            let num = this.state.array[i]
            let id = "insertion-" + i
            this.visulizeBox(position.row, position.col, 1, 1, num, id)
        }

        this.drawSeparator(this.separatorIndicator.row, this.separatorIndicator.col);
        this.drawIndicator(this.indicatorInitial.row, this.indicatorInitial.col, "min");
        this.drawHorizontalGrid()
    }

    getAction(array = this.state.array) {
        let arrayMap = [];
        for (let i = 0; i < array.length; i++) {
            arrayMap.push({
                number: array[i],
                index: i
            })
        }


        console.log(arrayMap)
    }

    render() {
        console.log(this.getAction())
        return(
            <div id="selection"></div>
        )
    }
}
