import React from 'react';
import {GridStructure} from './GridStructure'

export class MergeSort extends GridStructure {
    constructor(props) {
        // 1: Grid Width 30px
        // 2: Grid Height 30px
        // 3: nRow: 4
        // 4: nCol: 15
        // 5: padding: 0.05
        // 6: margin: top: 20px, bottom: 20px, left: 20px, right: 20px


        super(props, 30, 30, 4, 15, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "MergeSort")

        this.drawMergeGrid = this.drawMergeGrid.bind(this);

        this.state = {
            step: 0,
            speed: 200,
            setNewArray: false,
            animation: false
        }
    }

    componentDidMount() {
        this.svg = this.getSVG();
        this.drawBasicStructure();
        this.drawHorizontalGrid();
        this.drawMergeGrid();
        //this.initViz();
        //this.setState({
        //    actions: this.getAction()
        //})
    }

    drawMergeGrid() {
        for (let i = 1; i <= 14; i++) {
            this.drawLine(i * 30, 0, i * 30, 30)
        }
    }

    render() {
        return(<div id="MergeSort">

        </div>)
    }
}
