import React, { Component } from 'react';
import * as d3 from "d3";
import {GridStructure} from './GridStructure'

export class Insertion extends GridStructure {
    constructor(props) {
        super(props, 30, 30, 3, 10, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "insertion")

        this.svg = null;

        this.getInitialPosition = this.getInitialPosition.bind(this);

        this.separatorInitial = {
            row: 1,
            col: 1
        }

        let initArray = [3, 2, 6, 2, 9, 8]

        this.state = {
            array: initArray,
            actions: [],
            step: 0,
            speed: 200,
            setNewArray: false,
            animation: false
        }
    }

    getInitialPosition(array = this.state.array) {
        let positionMap = {}
        for (let i = 0; i < array.length; i++) {
            positionMap[i] = {
                row: 2,
                col: i + 2
            }
        } 
        return(positionMap)
    }

    render() {
        return(
            <div></div>
        )
    }

}
