import React, { Component } from 'react';
import * as d3 from "d3";
import {GridStructure} from './GridStructure'

export class MergeSortBasic extends GridStructure {
    constructor(props) {
        // 1: Grid Width 30px
        // 2: Grid Height 30px
        // 3: nRow: 3
        // 4: nCol: 10
        // 5: padding: 0.05
        // 6: margin: top: 20px, bottom: 20px, left: 20px, right: 20px


        super(props, 30, 30, 3, 10, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "selection")

        this.svg = null;

        this.indicatorInitial = {
            row: 3,
            col: 10
        }

        this.separatorInitial = {
            row: 1,
            col: 1
        }

        let initArray = [3, 2, 6, 2, 9, 8]

        let actions = this.getAction(initArray)

        this.state = {
            array: initArray,
            actions: actions,
            step: 0,
            speed: 200,
            setNewArray: false,
            animation: false
        }
    }

    render() {
        return(
            <div id="MergeBasic"></div>
        )
    }


}
