import React, { Component } from 'react';
import * as d3 from "d3";
import { get } from 'https';


export class SortTwoArray extends Component {
    constructor(props) {
        super(props)

        this.state = {
            actions: [],
            step: 0
        }
        this.firstArray = [5, 7, 8, 9, 10]
        this.secondArray = [2, 3, 6, 8, 9]

        this.getSteps = this.getSteps.bind(this);
        this.svgInitialize = this.svgInitialize.bind(this);
        this.getX = this.getX.bind(this);
        this.getY = this.getY.bind(this);
        this.appendTag = this.appendTag.bind(this);
        this.svg = null;
        this.width = 360;
        this.height = 240;
        this.nRow = 8;
        this.nCol = 12;

        this.margin = {
            top: 20, right: 20, bottom: 20, left: 20
        }
    }

    getX(x) {
        return(x + this.margin.left);
    }

    getY(y) {
        return(y + this.margin.top)
    }

    componentDidMount() {
        this.getSteps(this.firstArray, this.secondArray);
        this.svgInitialize();
    }

    svgInitialize() {
        this.svg = d3.select("body").append("svg")
                    .attr("width", this.width + this.margin.left + this.margin.right)
                    .attr("height", this.height + this.margin.top + this.margin.bottom);
        for (let i = 0; i <= this.nRow; i++) {
            this.svg.append("line").style("stroke", "gray")
                        .attr("x1", this.getX(0)).attr("y1", this.getY(i * this.height / this.nRow))
                        .attr("x2", this.getX(this.width)).attr("y2", this.getY(i * this.height / this.nRow))
        }

        for (let i = 0; i <= this.nCol; i++) {
            this.svg.append("line").style("stroke", "gray")
                        .attr("x1", this.getX(i * this.width / this.nCol)).attr("y1", this.getY(0))
                        .attr("x2", this.getX(i * this.width / this.nCol)).attr("y2", this.getY(this.height))
        }

        this.svg.append("line").style("stroke", "gray")
                        .attr("x1", this.getX(0)).attr("y1", this.getY(0))
                        .attr("x2", this.getX(0)).attr("y2", this.getY(this.height))

        this.appendTag(2, 2, "First")

        this.appendTag(4, 3, "Second")

        this.appendTag(6, 3.3, "Compare")
        this.appendTag(7, 2.2, "Result")
    }

    appendTag(level, box, text) {
        let gridHeight = this.height / this.nRow;
        let gridWidth = this.width / this.nCol;

        let x = 0.2 * gridWidth;
        let y = (level - 1) * gridHeight + 0.1 * gridHeight;
        let width = gridWidth * 0.8 * box;
        let height = 0.8 * gridHeight;

        let textX = 0.3 * gridWidth;
        let textY = (level - 1) * gridHeight + 0.7 * gridHeight;
        let textSize = gridWidth * 0.6;

        this.svg.append("rect")
                .attr("x", this.getX(x)).attr("y", this.getY(y))
                .attr("rx", 5).attr("ry", 5)
                .attr("width", width).attr("height", height).attr("fill", "steelblue")
        this.svg.append("text")
                .attr("x", this.getX(textX))
                .attr("y", this.getY(textY))
                .attr("fill", "white")
                .attr("font-size", textSize + "px")
                .text(text);
    }


    getSteps(firstArray, secondArray) {
        let newAction = []

        newAction.push("visulizeFirstArray")
        newAction.push("visulizeSecondArray")

        let i = 0;
        newAction.push("visulizeArrow")
        let j = 0;
        newAction.push("visulizeArrow")

        while(i < firstArray.length && j < secondArray.length) {
            let num1 = firstArray[i];
            let num2 = secondArray[j];
            newAction.push("Compare " + num1)
            newAction.push("Compare " + num2)
            if (num1 < num2) {
                newAction.push("Push First")
                newAction.push("Shift First Array")
                i++;
                newAction.push("Move Second back")
            } else {
                newAction.push("Push Second")
                newAction.push("Shift Second Array")
                j++;
                newAction.push("Move First back")
            }
        }

        while(i < firstArray.length) {
            newAction.push("Push First")
            newAction.push("Shift First Array")
            i++;
        }

        while(j < secondArray.length) {
            newAction.push("Push Second")
            newAction.push("Shift Second Array")
            j++;
        }

        console.log(newAction)
    }

    render() {
        //console.log(this.state.actions)
        return(
            <div>This is sort 1</div>
        )
    }
}
