import React, { Component } from 'react';
import * as d3 from "d3";


export class SortTwoArray extends Component {
    constructor(props) {
        super(props)

        this.state = {
            actions: [],
            step: 0
        }
        this.firstArray = [5, 7, 8, 9, 10]
        this.secondArray = [2, 3, 6, 8, 9]

        this.getSteps = this.getSteps.bind(this);
        this.svgInitialize = this.svgInitialize.bind(this);
        this.getX = this.getX.bind(this);
        this.getY = this.getY.bind(this);
        this.svg = null;
        this.width = 440;
        this.height = 340;
        this.margin = {
            top: 20, right: 20, bottom: 20, left: 20
        }
    }

    getX(x) {
        return(x + this.margin.left);
    }

    getY(y) {
        return(y + this.margin.top)
    }

    componentDidMount() {
        this.getSteps(this.firstArray, this.secondArray);
        this.svgInitialize();
    }

    svgInitialize() {
        this.svg = d3.select("body").append("svg").attr("width", this.width).attr("height", this.height);
        for (let i = 1; i <= 10; i++) {
            this.svg.append("line").style("stroke", "gray")
                        .attr("x1", this.getX(0)).attr("y1", this.getY(i * this.height / 10))
                        .attr("x2", this.getX(this.width)).attr("y2", this.getY(i * this.height / 10)) 
        }
    }


    getSteps(firstArray, secondArray) {
        let newAction = []

        newAction.push("visulizeFirstArray")
        newAction.push("visulizeSecondArray")

        let i = 0;
        newAction.push("visulizeArrow")
        let j = 0;
        newAction.push("visulizeArrow")

        while(i < firstArray.length && j < secondArray.length) {
            let num1 = firstArray[i];
            let num2 = secondArray[j];
            newAction.push("Compare " + num1)
            newAction.push("Compare " + num2)
            if (num1 < num2) {
                newAction.push("Push First")
                newAction.push("Shift First Array")
                i++;
                newAction.push("Move Second back")
            } else {
                newAction.push("Push Second")
                newAction.push("Shift Second Array")
                j++;
                newAction.push("Move First back")
            }
        }

        while(i < firstArray.length) {
            newAction.push("Push First")
            newAction.push("Shift First Array")
            i++;
        }

        while(j < secondArray.length) {
            newAction.push("Push Second")
            newAction.push("Shift Second Array")
            j++;
        }

        console.log(newAction)
    }

    render() {
        //console.log(this.state.actions)
        return(
            <div>This is sort 1</div>
        )
    }
}
