import React, { Component } from 'react';
import * as d3 from "d3";
import {GridStructure} from './GridStructure'

export class MergeSortBasic extends GridStructure {
    constructor(props) {
        // 1: Grid Width 30px
        // 2: Grid Height 30px
        // 3: nRow: 3
        // 4: nCol: 10
        // 5: padding: 0.05
        // 6: margin: top: 20px, bottom: 20px, left: 20px, right: 20px


        super(props, 30, 30, 3, 10, 0.05, {
            top: 20, bottom: 20, left: 20, right: 20
        }, "MergeBasic")

        this.svg = null;

        this.indicatorInitial = {
            row: 3,
            col: 10
        }

        this.separatorInitial = {
            row: 1,
            col: 1
        }

        this.initViz = this.initViz.bind(this)

        let first = [1, 4, 5, 7]
        let second = [2, 6, 8, 9]

        this.state = {
            first: first,
            second: second,
            actions: [],
            step: 0,
            speed: 200,
            setNewArray: false,
            animation: false
        }
    }

    componentDidMount() {
        this.svg = this.getSVG();
        this.drawBasicStructure();
        this.initViz();
        //this.setState({
        //    actions: this.getAction()
        //})
    }

    initViz() {
        this.drawHorizontalGrid();
        this.visulizeBox(1, 1, 2, 1, "1st", "First-Tag", "#756bb1", "white")
        this.visulizeBox(2, 1, 2, 1, "2nd", "Second-Tag", "#756bb1", "white")
        this.visulizeBox(3, 1, 2, 1, "Result", "Result-Tag", "#addd8e", "white")
    }



    render() {
        return(
            <div id="MergeBasic"></div>
        )
    }


}
